window.onload = function(){

      /* конфигурация */
    var width = 340; // ширина изображения
    var container = document.getElementById('content-wrapper');
    var containerWidth = container.clientWidth || container.offsetWidth;
    var count;  // количество изображений
    function getScreen() {
    	if(containerWidth >= 1150) {count = 3; return count};
    	// if(containerWidth > 1148) {count = 2; return count};
    	if(containerWidth > 974) {count = 2; return count};

    	return count = 1;
    }

  

    var carousel = document.getElementById('carousel');
    var list = carousel.querySelector('ul');
    var listElems = carousel.querySelectorAll('li');

    var position = 0; // текущий сдвиг влево

    carousel.querySelector('.prev').onclick = function() {

      getScreen();
      position = Math.min(position + width * 1, 0);
      list.style.marginLeft = position + 'px';
    };

    carousel.querySelector('.next').onclick = function() {

      getScreen();
      position = Math.max(position - width * 1, -width * (listElems.length - count));
      list.style.marginLeft = position + 'px';
    };


    var navigationSeats = document.getElementById('navi-text');
    var lineGreen = document.getElementById('nav-line-green');

    navigationSeats.querySelector('#x-small').onclick = function() {
      lineGreen.style.right = '95%';
    };
    navigationSeats.querySelector('#small').onclick = function() {
      lineGreen.style.right = '64%';
    };
    navigationSeats.querySelector('#medium').onclick = function() {
      lineGreen.style.right = '34%';
    };
    navigationSeats.querySelector('#large').onclick = function() {
      lineGreen.style.right = '3%';
    };


  





}

$('.carousel').carousel({
  interval: false,
})